// imports
import { useAppDispatch } from '../../hooks/hooks';
import { PackagesInterface } from '../../features/packages/types';
import { paymentSetPackageIds } from '../../features/payment/paymentSlice';

// antd
import { Badge, Image, Tag, Typography } from 'antd';
import { packagesToggle } from '../../features/packages/packagesSlice';

// Styles
import './Package.scss';

const { Title } = Typography;

// Interface
interface PropsInterface {
	packageItem: PackagesInterface;
}

// element
function Package({ packageItem }: PropsInterface) {
	// Redux
	const dispatch = useAppDispatch();

	// Functions
	// Handle Package Click
	function handlePackageSelect(packageItem: PackagesInterface) {
		// Tiklanan paketin selected degerini durumuna gore degistir.
		dispatch(packagesToggle(packageItem));
		// Tiklanan paketin id'sini payment state'ne ekle veya kaldir
		dispatch(paymentSetPackageIds({ id: packageItem.id }));
	}

	return (
		<div
			key={packageItem.id}
			className={`package${packageItem.selected ? ' package--selected' : ''}`}
			onClick={() => handlePackageSelect(packageItem)}
		>
			<div className="package__meta">
				<Image
					className="package__image"
					width="100%"
					height="100%"
					style={{ objectFit: 'cover', objectPosition: 'center center' }}
					src={packageItem.imagePath}
					preview={false}
					fallback="data:image/png;base64,"
				/>
			</div>
			<div className="package__details">
				<div className="package__details-header">
					<Title className="package__title" level={3}>
						{packageItem.name}
					</Title>
					<span className="package__amount">
						{packageItem.amount}
						{packageItem.currency}
					</span>
				</div>
				<div className="package__details-content">
					{packageItem.details?.length > 0 &&
						packageItem.details.map((detail, index) => (
							<Badge key={index} status="default" text={detail} />
						))}
				</div>
				<div className="package__details-footer">
					{packageItem.tags?.length > 0 &&
						packageItem.tags.map((tag, index) => (
							<Tag key={index} color="magenta">
								{tag}
							</Tag>
						))}
				</div>
			</div>
		</div>
	);
}

export default Package;
